import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const CheckoutPage = () => {
  const navigate = useNavigate();
  const [paymentMethod, setPaymentMethod] = useState('qr');
  const [orderInfo, setOrderInfo] = useState({
    name: '',
    phone: '',
    address: '',
    email: '',
    note: ''
  });

  // Thông tin ngân hàng của bạn
  const bankInfo = {
    bankId: 'CTG', // VietinBank
    accountNo: '113366668888',
    accountName: 'NGUYEN VAN A',
    amount: 2450000, // Tổng tiền đơn hàng
    template: 'compact2' // compact, compact2, print
  };

  const cartItems = [
    { 
      id: 1, 
      name: "Giày Chạy Bộ Nike Air Max 270", 
      price: 2450000, 
      quantity: 1,
      image: "https://images.unsplash.com/photo-1542291026-7eec264c27ff?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60"
    }
  ];

  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN').format(price) + ' đ';
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  // Tạo URL QR Code từ VietQR
  const generateVietQRUrl = () => {
    const total = calculateTotal();
    const orderId = 'DH' + Date.now();
    const addInfo = `SneakerHub ${orderId}`;
    
    // URL theo tiêu chuẩn VietQR
    return `https://img.vietqr.io/image/${bankInfo.bankId}-${bankInfo.accountNo}-${bankInfo.template}.png?amount=${total}&addInfo=${encodeURIComponent(addInfo)}&accountName=${encodeURIComponent(bankInfo.accountName)}`;
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderInfo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmitOrder = () => {
    if (!orderInfo.name || !orderInfo.phone || !orderInfo.address) {
      alert('Vui lòng điền đầy đủ thông tin giao hàng');
      return;
    }

    const orderData = {
      ...orderInfo,
      paymentMethod,
      total: calculateTotal(),
      items: cartItems,
      orderDate: new Date().toISOString(),
      orderId: 'DH' + Date.now()
    };

    alert(`Đặt hàng thành công!\nMã đơn hàng: ${orderData.orderId}\nTổng tiền: ${formatPrice(orderData.total)}\nPhương thức: ${paymentMethod === 'qr' ? 'Chuyển khoản QR' : 'Tiền mặt khi nhận hàng'}`);
    
    // Chuyển về trang chủ sau 3 giây
    setTimeout(() => {
      navigate('/');
    }, 3000);
  };

  const handleCopyBankInfo = () => {
    const bankInfoText = `VietinBank - STK: ${bankInfo.accountNo} - CHỦ TK: ${bankInfo.accountName}`;
    navigator.clipboard.writeText(bankInfoText)
      .then(() => alert('Đã sao chép thông tin ngân hàng!'))
      .catch(err => console.error('Lỗi sao chép:', err));
  };

  const handleDownloadQR = () => {
    const qrUrl = generateVietQRUrl();
    const link = document.createElement('a');
    link.href = qrUrl;
    link.download = `qr-code-${Date.now()}.png`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <>
      <div className="page-header">
        <h1>Thanh Toán</h1>
        <p>Hoàn tất đơn hàng của bạn</p>
      </div>
      
      <div className="checkout-page container">
        <div className="checkout-container">
          <div className="order-info">
            <h2>Thông Tin Đơn Hàng</h2>
            
            <div className="order-summary">
              {cartItems.map(item => (
                <div className="order-item" key={item.id}>
                  <img src={item.image} alt={item.name} />
                  <div className="item-details">
                    <h4>{item.name}</h4>
                    <p>Số lượng: {item.quantity}</p>
                    <p className="price">{formatPrice(item.price)}</p>
                  </div>
                  <div className="item-total">
                    {formatPrice(item.price * item.quantity)}
                  </div>
                </div>
              ))}
              
              <div className="order-totals">
                <div className="total-row">
                  <span>Tạm tính:</span>
                  <span>{formatPrice(calculateTotal())}</span>
                </div>
                <div className="total-row">
                  <span>Phí vận chuyển:</span>
                  <span className="free">MIỄN PHÍ</span>
                </div>
                <div className="total-row grand-total">
                  <span>Tổng cộng:</span>
                  <span>{formatPrice(calculateTotal())}</span>
                </div>
              </div>
            </div>
          </div>
          
          <div className="checkout-form">
            <div className="form-section">
              <h2>Thông Tin Giao Hàng</h2>
              <div className="form-group">
                <input
                  type="text"
                  name="name"
                  placeholder="Họ và tên *"
                  value={orderInfo.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="tel"
                  name="phone"
                  placeholder="Số điện thoại *"
                  value={orderInfo.phone}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="email"
                  name="email"
                  placeholder="Email"
                  value={orderInfo.email}
                  onChange={handleInputChange}
                />
              </div>
              <div className="form-group">
                <textarea
                  name="address"
                  placeholder="Địa chỉ giao hàng *"
                  value={orderInfo.address}
                  onChange={handleInputChange}
                  required
                  rows="3"
                />
              </div>
              <div className="form-group">
                <textarea
                  name="note"
                  placeholder="Ghi chú (tùy chọn)"
                  value={orderInfo.note}
                  onChange={handleInputChange}
                  rows="2"
                />
              </div>
            </div>
            
            <div className="form-section payment-section">
              <h2>Phương Thức Thanh Toán</h2>
              
              <div className="payment-options">
                <div className={`payment-option ${paymentMethod === 'qr' ? 'active' : ''}`}>
                  <input
                    type="radio"
                    id="qr"
                    name="payment"
                    checked={paymentMethod === 'qr'}
                    onChange={() => setPaymentMethod('qr')}
                  />
                  <label htmlFor="qr">
                    <i className="fas fa-qrcode"></i>
                    <span>Chuyển khoản QR VietQR</span>
                  </label>
                </div>
                
                <div className={`payment-option ${paymentMethod === 'cash' ? 'active' : ''}`}>
                  <input
                    type="radio"
                    id="cash"
                    name="payment"
                    checked={paymentMethod === 'cash'}
                    onChange={() => setPaymentMethod('cash')}
                  />
                  <label htmlFor="cash">
                    <i className="fas fa-money-bill-wave"></i>
                    <span>Tiền mặt khi nhận hàng</span>
                  </label>
                </div>
              </div>
              
              {paymentMethod === 'qr' && (
                <div className="qr-payment-details">
                  <div className="vietqr-header">
                    <img 
                      src="https://vietqr.io/logo/vietqr-logo.png" 
                      alt="VietQR Logo" 
                      className="vietqr-logo"
                    />
                    <h3>Thanh Toán Qua VietQR</h3>
                    <p className="vietqr-description">Quét mã QR bằng ứng dụng ngân hàng để thanh toán nhanh chóng</p>
                  </div>
                  
                  <div className="bank-info">
                    <h4>Thông Tin Chuyển Khoản</h4>
                    <div className="bank-details-grid">
                      <div className="bank-detail">
                        <span className="detail-label">Ngân hàng:</span>
                        <span className="detail-value">VietinBank</span>
                      </div>
                      <div className="bank-detail">
                        <span className="detail-label">Số tài khoản:</span>
                        <span className="detail-value">{bankInfo.accountNo}</span>
                      </div>
                      <div className="bank-detail">
                        <span className="detail-label">Chủ tài khoản:</span>
                        <span className="detail-value">{bankInfo.accountName}</span>
                      </div>
                      <div className="bank-detail">
                        <span className="detail-label">Số tiền:</span>
                        <span className="detail-value amount">{formatPrice(calculateTotal())}</span>
                      </div>
                      <div className="bank-detail">
                        <span className="detail-label">Nội dung:</span>
                        <span className="detail-value">SneakerHub DH{Date.now().toString().slice(-6)}</span>
                      </div>
                    </div>
                  </div>
                  
                  <div className="qr-code-container">
                    <div className="qr-code-wrapper">
                      <img 
                        src={generateVietQRUrl()} 
                        alt="QR Code Thanh Toán" 
                        className="qr-code"
                      />
                      <div className="qr-overlay">
                        <div className="qr-bank-logo">
                          <i className="fas fa-university"></i>
                          <span>VietinBank</span>
                        </div>
                        <div className="qr-amount">
                          <i className="fas fa-money-bill-wave"></i>
                          <span>{formatPrice(calculateTotal())}</span>
                        </div>
                      </div>
                    </div>
                    <p className="qr-instruction">
                      <i className="fas fa-mobile-alt"></i>
                      Mở ứng dụng ngân hàng, chọn "Quét mã QR" và quét mã bên trên
                    </p>
                  </div>
                  
                  <div className="qr-actions">
                    <button className="copy-btn" onClick={handleCopyBankInfo}>
                      <i className="fas fa-copy"></i> Sao chép thông tin
                    </button>
                    <button className="download-btn" onClick={handleDownloadQR}>
                      <i className="fas fa-download"></i> Tải QR Code
                    </button>
                  </div>
                  
                  <div className="payment-steps">
                    <h4>Hướng dẫn thanh toán:</h4>
                    <ol className="steps-list">
                      <li>Mở ứng dụng ngân hàng trên điện thoại</li>
                      <li>Chọn tính năng "Quét mã QR" hoặc "Scan QR"</li>
                      <li>Hướng camera vào mã QR bên trên</li>
                      <li>Kiểm tra thông tin và xác nhận thanh toán</li>
                      <li>Lưu biên lai để đối chiếu khi cần</li>
                    </ol>
                  </div>
                </div>
              )}
              
              {paymentMethod === 'cash' && (
                <div className="cash-payment-note">
                  <div className="note-box">
                    <i className="fas fa-info-circle"></i>
                    <div>
                      <h4>Thanh toán khi nhận hàng (COD)</h4>
                      <p>Bạn sẽ thanh toán bằng tiền mặt khi nhận được hàng. Vui lòng chuẩn bị số tiền chính xác để giao dịch thuận tiện.</p>
                      <ul className="cash-instructions">
                        <li>Kiểm tra sản phẩm trước khi thanh toán</li>
                        <li>Nhận hóa đơn từ nhân viên giao hàng</li>
                        <li>Hỗ trợ đổi trả trong 30 ngày nếu có vấn đề</li>
                      </ul>
                    </div>
                  </div>
                </div>
              )}
              
              <div className="order-actions">
                <button className="back-btn" onClick={() => navigate('/cart')}>
                  <i className="fas fa-arrow-left"></i> Quay lại giỏ hàng
                </button>
                <button className="submit-order-btn" onClick={handleSubmitOrder}>
                  <i className="fas fa-check-circle"></i> Đặt hàng ngay
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default CheckoutPage;
