import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const CheckoutPage = () => {
  const navigate = useNavigate();
  const [paymentMethod, setPaymentMethod] = useState('qr');
  const [orderInfo, setOrderInfo] = useState({
    name: '',
    phone: '',
    address: '',
    email: '',
    note: ''
  });

  // Th√¥ng tin ng√¢n h√†ng MB Bank
  const bankInfo = {
    bankId: 'MB', // MB Bank
    accountNo: '0935814328',
    accountName: 'TRAN VAN B', // Thay t√™n th·∫≠t c·ªßa b·∫°n
    amount: 2450000,
    template: 'compact'
  };

  const cartItems = [
    { 
      id: 1, 
      name: "Gi√†y Ch·∫°y B·ªô Nike Air Max 270", 
      price: 2450000, 
      quantity: 1,
      image: "https://images.unsplash.com/photo-1542291026-7eec264c27ff?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60"
    }
  ];

  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN').format(price) + ' ƒë';
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  // T·∫°o URL QR Code t·ª´ VietQR
  const generateVietQRUrl = () => {
    const total = calculateTotal();
    const orderId = 'SN' + Date.now().toString().slice(-6); // T·∫°o m√£ ƒë∆°n h√†ng 6 s·ªë
    const addInfo = `SneakerHub ${orderId}`;
    
    // URL theo ti√™u chu·∫©n VietQR v·ªõi MB Bank
    return `https://img.vietqr.io/image/${bankInfo.bankId}-${bankInfo.accountNo}-${bankInfo.template}.png?amount=${total}&addInfo=${encodeURIComponent(addInfo)}&accountName=${encodeURIComponent(bankInfo.accountName)}`;
  };

