import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const CartPage = ({ cartCount }) => {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([
    { 
      id: 1, 
      name: "Giày Chạy Bộ Nike Air Max 270", 
      price: 2450000, 
      quantity: 1, 
      image: "https://images.unsplash.com/photo-1542291026-7eec264c27ff?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60",
      size: 42
    },
    { 
      id: 2, 
      name: "Giày Sneaker Adidas Ultraboost 22", 
      price: 3200000, 
      quantity: 2, 
      image: "https://images.unsplash.com/photo-1606107557195-0e29a4b5b4aa?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60",
      size: 41
    },
    { 
      id: 3, 
      name: "Giày Tây Da Bò Đen Oxford", 
      price: 1850000, 
      quantity: 1, 
      image: "https://images.unsplash.com/photo-1595341888016-a392ef81b7de?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60",
      size: 40
    }
  ]);

  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN').format(price) + ' đ';
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const updateQuantity = (id, newQuantity) => {
    if (newQuantity < 1) return;
    setCartItems(cartItems.map(item => 
      item.id === id ? { ...item, quantity: newQuantity } : item
    ));
  };

  const removeItem = (id) => {
    setCartItems(cartItems.filter(item => item.id !== id));
  };

  const handleCheckout = () => {
    alert('Chuyển đến trang thanh toán!');
    // navigate('/checkout');
  };

  const handleContinueShopping = () => {
    navigate('/products');
  };

  return (
    <>
      <div className="page-header">
        <h1>Giỏ Hàng Của Bạn</h1>
        <p>Bạn có {cartItems.length} sản phẩm trong giỏ hàng</p>
      </div>
      
      <div className="cart-page">
        {cartItems.length === 0 ? (
          <div className="empty-cart">
            <i className="fas fa-shopping-cart"></i>
            <h2>Giỏ hàng của bạn đang trống</h2>
            <p>Hãy khám phá và thêm sản phẩm yêu thích vào giỏ hàng</p>
            <button className="continue-shopping" onClick={handleContinueShopping}>
              <i className="fas fa-shopping-bag"></i> Tiếp tục mua sắm
            </button>
          </div>
        ) : (
          <div className="cart-container">
            <div className="cart-items">
              {cartItems.map(item => (
                <div className="cart-item" key={item.id}>
                  <img src={item.image} alt={item.name} />
                  <div className="item-info">
                    <h3>{item.name}</h3>
                    <p className="price">{formatPrice(item.price)}</p>
                    <p>Size: {item.size}</p>
                    <div className="quantity-control">
                      <button onClick={() => updateQuantity(item.id, item.quantity - 1)}>
                        <i className="fas fa-minus"></i>
                      </button>
                      <span>{item.quantity}</span>
                      <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>
                        <i className="fas fa-plus"></i>
                      </button>
                    </div>
                  </div>
                  <div className="item-total">
                    <p>{formatPrice(item.price * item.quantity)}</p>
                    <button className="remove-btn" onClick={() => removeItem(item.id)} title="Xóa sản phẩm">
                      <i className="fas fa-trash"></i>
                    </button>
                  </div>
                </div>
              ))}
            </div>
            
            <div className="cart-summary">
              <h2>Tổng Thanh Toán</h2>
              <div className="summary-row">
                <span>Tạm tính ({cartItems.length} sản phẩm):</span>
                <span>{formatPrice(calculateTotal())}</span>
              </div>
              <div className="summary-row">
                <span>Phí vận chuyển:</span>
                <span style={{ color: '#27ae60', fontWeight: '600' }}>MIỄN PHÍ</span>
              </div>
              <div className="summary-row">
                <span>Giảm giá:</span>
                <span style={{ color: '#e74c3c', fontWeight: '600' }}>-500,000 đ</span>
              </div>
              <div className="summary-row total">
                <span>Tổng cộng:</span>
                <span>{formatPrice(calculateTotal() - 500000)}</span>
              </div>
              
              <button className="checkout-btn" onClick={handleCheckout}>
                <i className="fas fa-credit-card"></i> Tiến Hành Thanh Toán
              </button>
              <button className="continue-btn" onClick={handleContinueShopping}>
                <i className="fas fa-shopping-bag"></i> Tiếp Tục Mua Sắm
              </button>
              
              <div style={{ marginTop: '30px', padding: '20px', backgroundColor: '#f8f9fa', borderRadius: '8px' }}>
                <h4 style={{ marginBottom: '10px', color: '#2c3e50' }}>Ưu đãi đặc biệt</h4>
                <p style={{ fontSize: '14px', color: '#7f8c8d' }}>
                  <i className="fas fa-gift" style={{ color: '#e74c3c', marginRight: '8px' }}></i>
                  Miễn phí vận chuyển cho đơn hàng từ 1.500.000đ
                </p>
              </div>
            </div>
          </div>
        )}
      </div>
    </>
  );
};
// Tìm nút "Tiến Hành Thanh Toán" và sửa thành:
<button className="checkout-btn" onClick={() => navigate('/checkout')}></button>

export default CartPage;
