import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const CheckoutPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [paymentMethod, setPaymentMethod] = useState('qr');
  const [orderInfo, setOrderInfo] = useState({
    name: '',
    phone: '',
    address: '',
    email: '',
    note: ''
  });
  const [cartItems, setCartItems] = useState([]);
  const [selectedTemplate, setSelectedTemplate] = useState('compact');
  const [qrCodeUrl, setQrCodeUrl] = useState('');
  const [loadingQR, setLoadingQR] = useState(false);
  const [discount, setDiscount] = useState(500000);

  // D·ªØ li·ªáu s·∫£n ph·∫©m m·∫´u ƒë·ªÉ th√™m
  const sampleProducts = [
    {
      id: 1,
      name: "Gi√†y Ch·∫°y B·ªô Nike Air Max 270 React",
      price: 2450000,
      image: "https://images.unsplash.com/photo-1542291026-7eec264c27ff?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60",
      category: "running"
    },
    {
      id: 2,
      name: "Gi√†y Sneaker Adidas Ultraboost 22",
      price: 3200000,
      image: "https://images.unsplash.com/photo-1606107557195-0e29a4b5b4aa?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60",
      category: "sneaker"
    },
    {
      id: 3,
      name: "Gi√†y T√¢y Da B√≤ Oxford Cao C·∫•p",
      price: 1850000,
      image: "https://images.unsplash.com/photo-1595341888016-a392ef81b7de?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60",
      category: "formal"
    },
    {
      id: 4,
      name: "Gi√†y Th·ªÉ Thao Puma RS-X Turbo",
      price: 1890000,
      image: "https://images.unsplash.com/photo-1600269452121-4f2416e55c28?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60",
      category: "sneaker"
    }
  ];

  // Th√¥ng tin ng√¢n h√†ng MB Bank theo chu·∫©n VietQR
  const bankInfo = {
    bankId: 'MB',
    bankBin: '970422',
    accountNo: '0935814328',
    accountName: 'TRAN VAN B',
    template: 'compact'
  };

  // Kh·ªüi t·∫°o gi·ªè h√†ng v·ªõi d·ªØ li·ªáu m·∫´u
  useEffect(() => {
    // N·∫øu c√≥ d·ªØ li·ªáu t·ª´ CartPage, s·ª≠ d·ª•ng n√≥
    if (location.state?.cartItems) {
      setCartItems(location.state.cartItems);
    } else {
      // N·∫øu kh√¥ng, d√πng d·ªØ li·ªáu m·∫´u
      setCartItems(sampleProducts.slice(0, 2).map(item => ({...item, quantity: 1})));
    }
  }, [location]);

  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN').format(price) + ' ƒë';
  };

  // T√≠nh t·ªïng ti·ªÅn
  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + (item.price * (item.quantity || 1)), 0);
  };

  // T√≠nh t·ªïng sau gi·∫£m gi√°
  const calculateFinalTotal = () => {
    const total = calculateTotal();
    return total - discount;
  };

  // T√≠nh t·ªïng s·ªë s·∫£n ph·∫©m
  const getTotalItems = () => {
    return cartItems.reduce((total, item) => total + (item.quantity || 1), 0);
  };

  // Th√™m s·∫£n ph·∫©m m·ªõi
  const handleAddProduct = () => {
    // T√¨m s·∫£n ph·∫©m ch∆∞a c√≥ trong gi·ªè
    const availableProducts = sampleProducts.filter(product => 
      !cartItems.some(item => item.id === product.id)
    );
    
    if (availableProducts.length > 0) {
      const newProduct = {
        ...availableProducts[0],
        quantity: 1
      };
      setCartItems([...cartItems, newProduct]);
    } else {
      alert('ƒê√£ th√™m t·∫•t c·∫£ s·∫£n ph·∫©m m·∫´u!');
    }
  };

  // X√≥a s·∫£n ph·∫©m
  const handleRemoveItem = (id) => {
    if (window.confirm('B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a s·∫£n ph·∫©m n√†y?')) {
      setCartItems(cartItems.filter(item => item.id !== id));
    }
  };

  // C·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng
  const handleUpdateQuantity = (id, delta) => {
    setCartItems(cartItems.map(item => {
      if (item.id === id) {
        const newQuantity = (item.quantity || 1) + delta;
        return {
          ...item,
          quantity: newQuantity < 1 ? 1 : newQuantity
        };
      }
      return item;
    }));
  };

  // Toggle gi·∫£m gi√°
  const toggleDiscount = () => {
    setDiscount(prev => prev === 500000 ? 0 : 500000);
  };

  // T·∫°o URL QR Code
  const generateVietQRUrl = () => {
    const total = calculateFinalTotal();
    const orderId = 'SN' + Date.now().toString().slice(-6);
    
    return `https://img.vietqr.io/image/${bankInfo.bankId}-${bankInfo.accountNo}-${selectedTemplate}.png?amount=${total}&addInfo=SneakerHub%20${orderId}&accountName=${bankInfo.accountName}`;
  };

  // C·∫≠p nh·∫≠t QR code khi c√≥ thay ƒë·ªïi
  useEffect(() => {
    if (paymentMethod === 'qr') {
      setQrCodeUrl(generateVietQRUrl());
    }
  }, [cartItems, discount, selectedTemplate, paymentMethod]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderInfo(prev => ({
      ...prev,
      [name]: value
