    alert(`‚úÖ Thanh to√°n th√†nh c√¥ng!\n\nüì¶ M√£ ƒë∆°n h√†ng: ${orderId}\nüõí S·ªë s·∫£n ph·∫©m: ${getTotalItems()}\nüí∞ T·∫°m t√≠nh: ${formatPrice(calculateTotal())}\nüéÅ Gi·∫£m gi√°: ${formatPrice(discount)}\nüíµ T·ªïng thanh to√°n: ${formatPrice(calculateFinalTotal())}`);
    
    // Reset v√† quay v·ªÅ trang ch·ªß
    setTimeout(() => {
      navigate('/');
    }, 3000);
  };

  const handleContinueShopping = () => {
    navigate('/products');
  };

  return (
    <>
      <div className="page-header">
        <h1>Thanh To√°n</h1>
        <p>Ho√†n t·∫•t ƒë∆°n h√†ng c·ªßa b·∫°n</p>
      </div>
      
      <div className="checkout-page container">
        <div className="checkout-container">
          <div className="order-info">
            <h2>Th√¥ng Tin ƒê∆°n H√†ng</h2>
            
            <div className="order-summary">
              <div className="order-actions-header">
                <h3>Gi·ªè h√†ng ({getTotalItems()} s·∫£n ph·∫©m)</h3>
                <button className="add-product-btn" onClick={handleAddProduct}>
                  <i className="fas fa-plus"></i> Th√™m s·∫£n ph·∫©m m·ªõi
                </button>
              </div>
              
              {cartItems.length === 0 ? (
                <div className="empty-cart-message">
                  <i className="fas fa-shopping-cart"></i>
                  <p>Gi·ªè h√†ng tr·ªëng. Vui l√≤ng th√™m s·∫£n ph·∫©m!</p>
                </div>
              ) : (
                <>
                  {cartItems.map(item => (
                    <div className="order-item" key={item.id}>
                      <img src={item.image} alt={item.name} />
                      <div className="item-details">
                        <h4>{item.name}</h4>
                        <p className="item-category">{item.category}</p>
                        <p className="price">Gi√°: {formatPrice(item.price)}</p>
                        
                        <div className="quantity-control">
                          <button 
                            className="qty-btn" 
                            onClick={() => handleUpdateQuantity(item.id, -1)}
                          >
                            <i className="fas fa-minus"></i>
                          </button>
                          <span className="quantity">{item.quantity || 1}</span>
                          <button 
                            className="qty-btn"
                            onClick={() => handleUpdateQuantity(item.id, 1)}
                          >
                            <i className="fas fa-plus"></i>
                          </button>
                        </div>
                      </div>
                      <div className="item-total">
                        <span>{formatPrice(item.price * (item.quantity || 1))}</span>
                        <button 
                          className="remove-item-btn" 
                          onClick={() => handleRemoveItem(item.id)}
                        >
                          <i className="fas fa-trash"></i>
                        </button>
                      </div>
                    </div>
                  ))}
                </>
