import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const CheckoutPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [paymentMethod, setPaymentMethod] = useState('qr');
  const [orderInfo, setOrderInfo] = useState({
    name: '',
    phone: '',
    address: '',
    email: '',
    note: ''
  });
  const [cartItems, setCartItems] = useState([]);
  const [discount, setDiscount] = useState(500000);
  const [showVietQR, setShowVietQR] = useState(false);

  // D·ªØ li·ªáu s·∫£n ph·∫©m m·∫´u ƒë·ªÉ th√™m
  const sampleProducts = [
    {
      id: 1,
      name: "Gi√†y Ch·∫°y B·ªô Nike Air Max 270 React",
      price: 2450000,
      image: "https://images.unsplash.com/photo-1542291026-7eec264c27ff?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60",
      category: "running"
    },
    {
      id: 2,
      name: "Gi√†y Sneaker Adidas Ultraboost 22",
      price: 3200000,
      image: "https://images.unsplash.com/photo-1606107557195-0e29a4b5b4aa?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60",
      category: "sneaker"
    },
    {
      id: 3,
      name: "Gi√†y T√¢y Da B√≤ Oxford Cao C·∫•p",
      price: 1850000,
      image: "https://images.unsplash.com/photo-1595341888016-a392ef81b7de?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60",
      category: "formal"
    },
    {
      id: 4,
      name: "Gi√†y Th·ªÉ Thao Puma RS-X Turbo",
      price: 1890000,
      image: "https://images.unsplash.com/photo-1600269452121-4f2416e55c28?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60",
      category: "sneaker"
    }
  ];

  // Kh·ªüi t·∫°o gi·ªè h√†ng v·ªõi d·ªØ li·ªáu m·∫´u
  useEffect(() => {
    if (location.state?.cartItems) {
      setCartItems(location.state.cartItems);
    } else {
      setCartItems(sampleProducts.slice(0, 2).map(item => ({...item, quantity: 1})));
    }
  }, [location]);

  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN').format(price) + ' ƒë';
  };

  // T√≠nh t·ªïng ti·ªÅn
  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + (item.price * (item.quantity || 1)), 0);
  };

  // T√≠nh t·ªïng sau gi·∫£m gi√°
  const calculateFinalTotal = () => {
    const total = calculateTotal();
    return total - discount;
  };

  // T√≠nh t·ªïng s·ªë s·∫£n ph·∫©m
  const getTotalItems = () => {
    return cartItems.reduce((total, item) => total + (item.quantity || 1), 0);
  };

  // Th√™m s·∫£n ph·∫©m m·ªõi
  const handleAddProduct = () => {
    const availableProducts = sampleProducts.filter(product => 
      !cartItems.some(item => item.id === product.id)
    );
    
    if (availableProducts.length > 0) {
      const newProduct = {
        ...availableProducts[0],
        quantity: 1
      };
      setCartItems([...cartItems, newProduct]);
      alert(`ƒê√£ th√™m s·∫£n ph·∫©m: ${newProduct.name}`);
    } else {
      alert('ƒê√£ th√™m t·∫•t c·∫£ s·∫£n ph·∫©m m·∫´u!');
    }
  };

  // X√≥a s·∫£n ph·∫©m
  const handleRemoveItem = (id) => {
    if (window.confirm('B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a s·∫£n ph·∫©m n√†y?')) {
      setCartItems(cartItems.filter(item => item.id !== id));
    }
  };

  // C·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng
  const handleUpdateQuantity = (id, delta) => {
    setCartItems(cartItems.map(item => {
      if (item.id === id) {
        const newQuantity = (item.quantity || 1) + delta;
        return {
          ...item,
          quantity: newQuantity < 1 ? 1 : newQuantity
        };
      }
      return item;
    }));
  };

  // Toggle gi·∫£m gi√°
  const toggleDiscount = () => {
    const newDiscount = discount === 500000 ? 0 : 500000;
    setDiscount(newDiscount);
    alert(newDiscount > 0 ? 'ƒê√£ √°p d·ª•ng gi·∫£m gi√° 500,000ƒë' : 'ƒê√£ h·ªßy gi·∫£m gi√°');
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderInfo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmitOrder = () => {
    if (!orderInfo.name || !orderInfo.phone || !orderInfo.address) {
      alert('Vui l√≤ng ƒëi·ªÅn ƒë·∫ßy ƒë·ªß th√¥ng tin giao h√†ng');
      return;
    }

    if (paymentMethod === 'qr' && !showVietQR) {
      setShowVietQR(true);
      return;
    }

    const orderId = 'SN' + Date.now().toString().slice(-6);
    const orderData = {
      ...orderInfo,
      paymentMethod,
      subtotal: calculateTotal(),
      discount: discount,
      finalTotal: calculateFinalTotal(),
      items: cartItems,
      orderDate: new Date().toLocaleString('vi-VN'),
      orderId: orderId,
      totalItems: getTotalItems()
    };

    alert(`‚úÖ Thanh to√°n th√†nh c√¥ng!\n\nüì¶ M√£ ƒë∆°n h√†ng: ${orderId}\nüõí S·ªë s·∫£n ph·∫©m: ${getTotalItems()}\nüí∞ T·∫°m t√≠nh: ${formatPrice(calculateTotal())}\nüéÅ Gi·∫£m gi√°: ${formatPrice(discount)}\nüíµ T·ªïng thanh to√°n: ${formatPrice(calculateFinalTotal())}`);
    
    setTimeout(() => {
      navigate('/');
    }, 3000);
  };

  const handleContinueShopping = () => {
    navigate('/products');
  };

  const handleBackToCart = () => {
    navigate('/cart');
  };

  const handleCopyBankInfo = () => {
    const bankInfo = `TH√îNG TIN CHUY·ªÇN KHO·∫¢N VIETQR
----------------------------
üè¶ Ng√¢n h√†ng: MB Bank (Ng√¢n h√†ng TMCP Qu√¢n ƒë·ªôi)
üí∞ S·ªë t√†i kho·∫£n: 20202222032006
üë§ Ch·ªß t√†i kho·∫£n: TRAN VAN B
üíµ S·ªë ti·ªÅn: ${formatPrice(calculateFinalTotal())}
üìù N·ªôi dung: Thanh to√°n ƒë∆°n h√†ng SneakerHub

üëâ Qu√©t m√£ QR ƒë·ªÉ thanh to√°n nhanh ch√≥ng!`;
    
    navigator.clipboard.writeText(bankInfo).then(() => {
      alert('‚úÖ ƒê√£ sao ch√©p th√¥ng tin ng√¢n h√†ng v√†o clipboard!');
    }).catch(err => {
      // Fallback cho tr√¨nh duy·ªát kh√¥ng h·ªó tr·ª£ clipboard API
      const textArea = document.createElement('textarea');
      textArea.value = bankInfo;
      document.body.appendChild(textArea);
      textArea.select();
      document.execCommand('copy');
      document.body.removeChild(textArea);
      alert('‚úÖ ƒê√£ sao ch√©p th√¥ng tin ng√¢n h√†ng!');
    });
  };

  const handlePrintQR = () => {
    const printWindow = window.open('', '_blank');
    printWindow.document.write(`
      <!DOCTYPE html>
      <html>
      <head>
        <title>M√£ QR Thanh To√°n - SneakerHub</title>
        <style>
          body { font-family: Arial, sans-serif; text-align: center; padding: 20px; }
          .print-container { max-width: 600px; margin: 0 auto; }
          .logo { font-size: 24px; font-weight: bold; color: #e74c3c; margin-bottom: 20px; }
          .qr-code { max-width: 400px; margin: 20px auto; border: 2px solid #ddd; padding: 10px; }
          .bank-info { text-align: left; margin: 20px 0; padding: 20px; border: 1px solid #ddd; border-radius: 8px; background: #f9f9f9; }
          .info-item { margin: 10px 0; display: flex; }
          .info-label { font-weight: bold; min-width: 150px; }
          .amount { color: #e74c3c; font-weight: bold; font-size: 20px; }
          .note { font-style: italic; color: #666; margin-top: 20px; font-size: 14px; }
        </style>
      </head>
      <body>
        <div class="print-container">
          <div class="logo">SneakerHub</div>
          <h2>M√É QR THANH TO√ÅN</h2>
          
          <div class="bank-info">
            <div class="info-item"><span class="info-label">Ng√¢n h√†ng:</span> MB Bank (Ng√¢n h√†ng TMCP Qu√¢n ƒë·ªôi)</div>
            <div class="info-item"><span class="info-label">S·ªë t√†i kho·∫£n:</span> 20202222032006</div>
            <div class="info-item"><span class="info-label">Ch·ªß t√†i kho·∫£n:</span> TRAN VAN B</div>
            <div class="info-item"><span class="info-label">S·ªë ti·ªÅn:</span> <span class="amount">${formatPrice(calculateFinalTotal())}</span></div>
            <div class="info-item"><span class="info-label">N·ªôi dung:</span> Thanh to√°n ƒë∆°n h√†ng SneakerHub</div>
          </div>
          
          <div class="qr-code">
            <img src="https://img.vietqr.io/image/MB-20202222032006-compact.png" alt="VietQR Code" style="width: 100%;">
          </div>
          
          <div class="note">
            <p>üîπ Qu√©t m√£ QR b·∫±ng ·ª©ng d·ª•ng MB Bank ho·∫∑c b·∫•t k·ª≥ ·ª©ng d·ª•ng ng√¢n h√†ng n√†o h·ªó tr·ª£ VietQR</p>
            <p>üîπ M√£ QR n√†y tu√¢n th·ªß ti√™u chu·∫©n VietQR - Chu·∫©n QR code qu·ªëc gia Vi·ªát Nam</p>
            <p>üîπ Ng√†y in: ${new Date().toLocaleString('vi-VN')}</p>
          </div>
        </div>
        <script>
          window.onload = function() {
            window.print();
            setTimeout(function() {
              window.close();
            }, 1000);
          }
        </script>
      </body>
      </html>
    `);
  };

  const handleDownloadQR = () => {
    const link = document.createElement('a');
    link.href = 'https://img.vietqr.io/image/MB-20202222032006-compact.png';
    link.download = `vietqr-mb-${Date.now()}.png`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    alert('‚úÖ ƒê√£ t·∫£i QR Code v·ªÅ m√°y!');
  };

  return (
    <>
      <div className="page-header">
        <h1>Thanh To√°n</h1>
        <p>Ho√†n t·∫•t ƒë∆°n h√†ng c·ªßa b·∫°n</p>
      </div>
      
      <div className="checkout-page container">
        <div className="checkout-container">
          <div className="order-info">
            <h2>Th√¥ng Tin ƒê∆°n H√†ng</h2>
            
            <div className="order-summary">
              <div className="order-actions-header">
                <h3>Gi·ªè h√†ng ({getTotalItems()} s·∫£n ph·∫©m)</h3>
                <button className="add-product-btn" onClick={handleAddProduct}>
                  <i className="fas fa-plus"></i> Th√™m s·∫£n ph·∫©m m·ªõi
                </button>
              </div>
              
              {cartItems.length === 0 ? (
                <div className="empty-cart-message">
                  <i className="fas fa-shopping-cart"></i>
                  <p>Gi·ªè h√†ng tr·ªëng. Vui l√≤ng th√™m s·∫£n ph·∫©m!</p>
                </div>
              ) : (
                <>
                  {cartItems.map(item => (
                    <div className="order-item" key={item.id}>
                      <img src={item.image} alt={item.name} />
                      <div className="item-details">
                        <h4>{item.name}</h4>
                        <p className="item-category">{item.category}</p>
                        <p className="price">Gi√°: {formatPrice(item.price)}</p>
                        
                        <div className="quantity-control">
                          <button 
                            className="qty-btn" 
                            onClick={() => handleUpdateQuantity(item.id, -1)}
                          >
                            <i className="fas fa-minus"></i>
                          </button>
                          <span className="quantity">{item.quantity || 1}</span>
                          <button 
                            className="qty-btn"
                            onClick={() => handleUpdateQuantity(item.id, 1)}
                          >
                            <i className="fas fa-plus"></i>
                          </button>
                        </div>
                      </div>
                      <div className="item-total">
                        <span>{formatPrice(item.price * (item.quantity || 1))}</span>
                        <button 
                          className="remove-item-btn" 
                          onClick={() => handleRemoveItem(item.id)}
                        >
                          <i className="fas fa-trash"></i>
                        </button>
                      </div>
                    </div>
                  ))}
                </>
              )}
              
              <div className="order-totals">
                <div className="total-row">
                  <span>T·∫°m t√≠nh ({getTotalItems()} s·∫£n ph·∫©m):</span>
                  <span>{formatPrice(calculateTotal())}</span>
                </div>
                <div className="total-row">
                  <span>Ph√≠ v·∫≠n chuy·ªÉn:</span>
                  <span className="free">MI·ªÑN PH√ç</span>
                </div>
                <div className="total-row">
                  <span>Gi·∫£m gi√°:</span>
                  <div className="discount-control">
                    <span className={`discount-amount ${discount > 0 ? 'active' : ''}`}>
                      -{formatPrice(discount)}
                    </span>
                    <button 
                      className={`toggle-discount-btn ${discount > 0 ? 'active' : ''}`}
                      onClick={toggleDiscount}
                    >
                      {discount > 0 ? '‚ùå H·ªßy gi·∫£m gi√°' : 'üéÅ √Åp d·ª•ng gi·∫£m gi√°'}
                    </button>
                  </div>
                </div>
                <div className="total-row grand-total">
                  <span>T·ªïng c·ªông:</span>
                  <span className="final-total">{formatPrice(calculateFinalTotal())}</span>
                </div>
                
                <div className="savings-note">
                  <i className="fas fa-piggy-bank"></i>
                  <span>B·∫°n ƒë√£ ti·∫øt ki·ªám ƒë∆∞·ª£c {formatPrice(discount)}</span>
                </div>
              </div>
            </div>
          </div>
          
          <div className="checkout-form">
            {!showVietQR ? (
              <>
                <div className="form-section">
                  <h2>Th√¥ng Tin Giao H√†ng</h2>
                  <div className="form-group">
                    <input
                      type="text"
                      name="name"
                      placeholder="H·ªç v√† t√™n *"
                      value={orderInfo.name}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <input
                      type="tel"
                      name="phone"
                      placeholder="S·ªë ƒëi·ªán tho·∫°i *"
                      value={orderInfo.phone}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <input
                      type="email"
                      name="email"
                      placeholder="Email (nh·∫≠n h√≥a ƒë∆°n)"
                      value={orderInfo.email}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className="form-group">
                    <textarea
                      name="address"
                      placeholder="ƒê·ªãa ch·ªâ giao h√†ng *"
                      value={orderInfo.address}
                      onChange={handleInputChange}
                      required
                      rows="3"
                    />
                  </div>
                  <div className="form-group">
                    <textarea
                      name="note"
                      placeholder="Ghi ch√∫ cho ng∆∞·ªùi giao h√†ng (t√πy ch·ªçn)"
                      value={orderInfo.note}
                      onChange={handleInputChange}
                      rows="2"
                    />
                  </div>
                </div>
                
                <div className="form-section payment-section">
                  <h2>Ph∆∞∆°ng Th·ª©c Thanh To√°n</h2>
                  
                  <div className="payment-options">
                    <div className={`payment-option ${paymentMethod === 'qr' ? 'active' : ''}`}>
                      <input
                        type="radio"
                        id="qr"
                        name="payment"
                        checked={paymentMethod === 'qr'}
                        onChange={() => setPaymentMethod('qr')}
                      />
                      <label htmlFor="qr">
                        <i className="fas fa-qrcode"></i>
                        <div>
