import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const CheckoutPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [paymentMethod, setPaymentMethod] = useState('qr');
  const [orderInfo, setOrderInfo] = useState({
    name: '',
    phone: '',
    address: '',
    email: '',
    note: ''
  });
  const [cartItems, setCartItems] = useState([]);
  const [discount, setDiscount] = useState(500000);
  const [showVietQR, setShowVietQR] = useState(false);

  // Dá»¯ liá»‡u sáº£n pháº©m máº«u Ä‘á»ƒ thÃªm
  const sampleProducts = [
    {
      id: 1,
      name: "GiÃ y Cháº¡y Bá»™ Nike Air Max 270 React",
      price: 2450000,
      image: "https://images.unsplash.com/photo-1542291026-7eec264c27ff?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60",
      category: "running"
    },
    {
      id: 2,
      name: "GiÃ y Sneaker Adidas Ultraboost 22",
      price: 3200000,
      image: "https://images.unsplash.com/photo-1606107557195-0e29a4b5b4aa?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60",
      category: "sneaker"
    },
    {
      id: 3,
      name: "GiÃ y TÃ¢y Da BÃ² Oxford Cao Cáº¥p",
      price: 1850000,
      image: "https://images.unsplash.com/photo-1595341888016-a392ef81b7de?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60",
      category: "formal"
    },
    {
      id: 4,
      name: "GiÃ y Thá»ƒ Thao Puma RS-X Turbo",
      price: 1890000,
      image: "https://images.unsplash.com/photo-1600269452121-4f2416e55c28?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60",
      category: "sneaker"
    }
  ];

  // Khá»Ÿi táº¡o giá» hÃ ng vá»›i dá»¯ liá»‡u máº«u
  useEffect(() => {
    if (location.state?.cartItems) {
      setCartItems(location.state.cartItems);
    } else {
      setCartItems(sampleProducts.slice(0, 2).map(item => ({...item, quantity: 1})));
    }
  }, [location]);

  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN').format(price) + ' Ä‘';
  };

  // TÃ­nh tá»•ng tiá»n
  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + (item.price * (item.quantity || 1)), 0);
  };

  // TÃ­nh tá»•ng sau giáº£m giÃ¡
  const calculateFinalTotal = () => {
    const total = calculateTotal();
    return total - discount;
  };

  // TÃ­nh tá»•ng sá»‘ sáº£n pháº©m
  const getTotalItems = () => {
    return cartItems.reduce((total, item) => total + (item.quantity || 1), 0);
  };

  // ThÃªm sáº£n pháº©m má»›i
  const handleAddProduct = () => {
    const availableProducts = sampleProducts.filter(product => 
      !cartItems.some(item => item.id === product.id)
    );
    
    if (availableProducts.length > 0) {
      const newProduct = {
        ...availableProducts[0],
        quantity: 1
      };
      setCartItems([...cartItems, newProduct]);
    } else {
      alert('ÄÃ£ thÃªm táº¥t cáº£ sáº£n pháº©m máº«u!');
    }
  };

  // XÃ³a sáº£n pháº©m
  const handleRemoveItem = (id) => {
    if (window.confirm('Báº¡n cÃ³ cháº¯c muá»‘n xÃ³a sáº£n pháº©m nÃ y?')) {
      setCartItems(cartItems.filter(item => item.id !== id));
    }
  };

  // Cáº­p nháº­t sá»‘ lÆ°á»£ng
  const handleUpdateQuantity = (id, delta) => {
    setCartItems(cartItems.map(item => {
      if (item.id === id) {
        const newQuantity = (item.quantity || 1) + delta;
        return {
          ...item,
          quantity: newQuantity < 1 ? 1 : newQuantity
        };
      }
      return item;
    }));
  };

  // Toggle giáº£m giÃ¡
  const toggleDiscount = () => {
    setDiscount(prev => prev === 500000 ? 0 : 500000);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderInfo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmitOrder = () => {
    if (!orderInfo.name || !orderInfo.phone || !orderInfo.address) {
      alert('Vui lÃ²ng Ä‘iá»n Ä‘áº§y Ä‘á»§ thÃ´ng tin giao hÃ ng');
      return;
    }

    if (paymentMethod === 'qr' && !showVietQR) {
      setShowVietQR(true);
      return;
    }

    const orderId = 'SN' + Date.now().toString().slice(-6);
    const orderData = {
      ...orderInfo,
      paymentMethod,
      subtotal: calculateTotal(),
      discount: discount,
      finalTotal: calculateFinalTotal(),
      items: cartItems,
      orderDate: new Date().toLocaleString('vi-VN'),
      orderId: orderId,
      totalItems: getTotalItems()
    };

    alert(`âœ… Thanh toÃ¡n thÃ nh cÃ´ng!\n\nðŸ“¦ MÃ£ Ä‘Æ¡n hÃ ng: ${orderId}\nðŸ›’ Sá»‘ sáº£n pháº©m: ${getTotalItems()}\nðŸ’° Táº¡m tÃ­nh: ${formatPrice(calculateTotal())}\nðŸŽ Giáº£m giÃ¡: ${formatPrice(discount)}\nðŸ’µ Tá»•ng thanh toÃ¡n: ${formatPrice(calculateFinalTotal())}`);
    
    setTimeout(() => {
      navigate('/');
    }, 3000);
  };

  const handleContinueShopping = () => {
    navigate('/products');
  };

  const handleBackToCart = () => {
    navigate('/cart');
  };

  const handleCopyBankInfo = () => {
    const bankInfo = `MB Bank
Sá»‘ tÃ i khoáº£n: 20202222032006
Chá»§ tÃ i khoáº£n: NGUYEN VAN A
Sá»‘ tiá»n: ${formatPrice(calculateFinalTotal())}
Ná»™i dung: Thanh toÃ¡n don hang SneakerHub`;
    
    navigator.clipboard.writeText(bankInfo).then(() => {
      alert('ÄÃ£ sao chÃ©p thÃ´ng tin ngÃ¢n hÃ ng vÃ o clipboard!');
    });
  };

  return (
    <>
