import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const CheckoutPage = () => {
  const navigate = useNavigate();
  const [paymentMethod, setPaymentMethod] = useState('qr');
  const [orderInfo, setOrderInfo] = useState({
    name: '',
    phone: '',
    address: '',
    email: '',
    note: ''
  });
  const [selectedTemplate, setSelectedTemplate] = useState('compact');
  const [qrCodeUrl, setQrCodeUrl] = useState('');
  const [loadingQR, setLoadingQR] = useState(false);

  // Th√¥ng tin ng√¢n h√†ng MB Bank theo chu·∫©n VietQR
  const bankInfo = {
    bankId: 'MB', // MB Bank code
    bankBin: '970422', // BIN c·ªßa MB Bank
    accountNo: '0935814328',
    accountName: 'TRAN VAN B', // T√™n kh√¥ng d·∫•u, vi·∫øt hoa
    template: 'compact'
  };

  // C√°c template VietQR c√≥ s·∫µn
  const vietQRTemplates = [
    { id: 'compact', name: 'Compact', size: '540x540', description: 'QR + logo VietQR, Napas, ng√¢n h√†ng' },
    { id: 'compact2', name: 'Compact 2', size: '540x640', description: 'ƒê·∫ßy ƒë·ªß th√¥ng tin chuy·ªÉn kho·∫£n' },
    { id: 'qr_only', name: 'QR Only', size: '480x480', description: 'Ch·ªâ QR code ƒë∆°n gi·∫£n' },
    { id: 'print', name: 'Print', size: '600x776', description: 'ƒê·∫ßy ƒë·ªß th√¥ng tin ƒë·ªÉ in' }
  ];

  const cartItems = [
    { 
      id: 1, 
      name: "Gi√†y Ch·∫°y B·ªô Nike Air Max 270", 
      price: 2450000, 
      quantity: 1,
      image: "https://images.unsplash.com/photo-1542291026-7eec264c27ff?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=60"
    }
  ];

  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN').format(price) + ' ƒë';
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  // H√†m chu·∫©n h√≥a chu·ªói theo y√™u c·∫ßu VietQR (kh√¥ng d·∫•u, vi·∫øt hoa)
  const normalizeVietQRString = (str) => {
    return str
      .normalize('NFD')
      .replace(/[\u0300-\u036f]/g, '')
      .toUpperCase()
      .replace(/[^A-Z0-9\s]/g, '')
      .slice(0, 25); // Gi·ªõi h·∫°n 25 k√Ω t·ª±
  };

  // T·∫°o URL QR Code t·ª´ VietQR v·ªõi template ƒë∆∞·ª£c ch·ªçn
  const generateVietQRUrl = (template = selectedTemplate) => {
    const total = calculateTotal() - 500000; // T·ªïng sau gi·∫£m gi√°
    const orderId = 'SN' + Date.now().toString().slice(-6);
    const addInfo = normalizeVietQRString(`SneakerHub ${orderId}`);
    const accountName = normalizeVietQRString(bankInfo.accountName);
    
    // T·∫°o URL theo c·∫•u tr√∫c: https://img.vietqr.io/image/<BANK_ID>-<ACCOUNT_NO>-<TEMPLATE>.png
    return `https://img.vietqr.io/image/${bankInfo.bankId}-${bankInfo.accountNo}-${template}.png?amount=${total}&addInfo=${encodeURIComponent(addInfo)}&accountName=${encodeURIComponent(accountName)}`;
  };

  // L·∫•y danh s√°ch ng√¢n h√†ng t·ª´ API VietQR (public API)
  const fetchBanks = async () => {
    try {
      const response = await fetch('https://api.vietqr.io/v2/banks');
      const data = await response.json();
      if (data.code === '00') {
        console.log('Danh s√°ch ng√¢n h√†ng t·ª´ VietQR:', data.data);
        return data.data;
      }
    } catch (error) {
      console.error('L·ªói khi l·∫•y danh s√°ch ng√¢n h√†ng:', error);
    }
    return [];
  };

  // T·∫°o QR Code b·∫±ng API VietQR (c·∫ßn API Key)
  const generateVietQRWithAPI = async () => {
    setLoadingQR(true);
    
    const total = calculateTotal() - 500000;
    const orderId = 'SN' + Date.now().toString().slice(-6);
    
    const requestBody = {
      accountNo: bankInfo.accountNo,
      accountName: normalizeVietQRString(bankInfo.accountName),
      acqId: bankInfo.bankBin,
      amount: total.toString(),
      addInfo: normalizeVietQRString(`SneakerHub ${orderId}`),
      template: selectedTemplate,
      format: 'text'
    };

    try {
      const response = await fetch('https://api.vietqr.io/v2/generate', {
        method: 'POST',
        headers: {
          'x-client-id': 'YOUR_CLIENT_ID', // Thay b·∫±ng Client ID c·ªßa b·∫°n
          'x-api-key': 'YOUR_API_KEY',     // Thay b·∫±ng API Key c·ªßa b·∫°n
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(requestBody)
      });

      const data = await response.json();
      
      if (data.code === '00') {
        // N·∫øu c√≥ API key, c√≥ th·ªÉ l·∫•y qrDataURL t·ª´ data.data.qrDataURL
        console.log('QR t·∫°o th√†nh c√¥ng t·ª´ API:', data);
        setQrCodeUrl(generateVietQRUrl(selectedTemplate)); // Fallback d√πng URL
      } else {
        console.error('L·ªói API VietQR:', data.desc);
        setQrCodeUrl(generateVietQRUrl(selectedTemplate)); // Fallback
      }
    } catch (error) {
      console.error('L·ªói k·∫øt n·ªëi API VietQR:', error);
      setQrCodeUrl(generateVietQRUrl(selectedTemplate)); // Fallback
    } finally {
      setLoadingQR(false);
    }
  };

  useEffect(() => {
    // Kh·ªüi t·∫°o danh s√°ch ng√¢n h√†ng
    fetchBanks();
    
    // T·∫°o QR code ban ƒë·∫ßu
    setQrCodeUrl(generateVietQRUrl());
  }, []);

  useEffect(() => {
    // C·∫≠p nh·∫≠t QR code khi thay ƒë·ªïi template
    if (paymentMethod === 'qr') {
      setQrCodeUrl(generateVietQRUrl());
    }
  }, [selectedTemplate, paymentMethod]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderInfo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmitOrder = () => {
    if (!orderInfo.name || !orderInfo.phone || !orderInfo.address) {
      alert('Vui l√≤ng ƒëi·ªÅn ƒë·∫ßy ƒë·ªß th√¥ng tin giao h√†ng');
      return;
    }

    const orderId = 'SN' + Date.now().toString().slice(-6);
    const orderData = {
      ...orderInfo,
      paymentMethod,
      total: calculateTotal() - 500000,
      items: cartItems,
      orderDate: new Date().toLocaleString('vi-VN'),
      orderId: orderId,
      vietQRInfo: {
        bank: bankInfo,
        template: selectedTemplate,
        qrUrl: qrCodeUrl
      }
    };

    localStorage.setItem('lastOrder', JSON.stringify(orderData));
    
    alert(`‚úÖ ƒê·∫∑t h√†ng th√†nh c√¥ng!\n\nüì¶ M√£ ƒë∆°n h√†ng: ${orderId}\nüí∞ T·ªïng ti·ªÅn: ${formatPrice(orderData.total)}\nüí≥ Ph∆∞∆°ng th·ª©c: ${paymentMethod === 'qr' ? 'Chuy·ªÉn kho·∫£n QR VietQR' : 'Ti·ªÅn m·∫∑t khi nh·∫≠n h√†ng'}\n\nC·∫£m ∆°n b·∫°n ƒë√£ mua s·∫Øm t·∫°i SneakerHub!`);
    
    setTimeout(() => {
      navigate('/');
    }, 3000);
  };

  const handleCopyBankInfo = () => {
    const total = calculateTotal() - 500000;
