      image: "https://images.unsplash.com/photo-1525966222134-fcfa99b8ae77?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80",
      description: "Giày canvas cổ điển, biểu tượng văn hóa đường phố, nhiều màu sắc",
      category: "sneaker",
      rating: 4.8,
      discount: 15,
      colors: ["Trắng", "Đen", "Đỏ", "Xanh"],
      sizes: [38, 39, 40, 41, 42]
    },
    {
      id: 8,
      name: "Giày Bóng Đá Adidas Predator Edge",
      price: 2200000,
      image: "https://images.unsplash.com/photo-1600185365483-26d7a4cc7519?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80",
      description: "Giày bóng đá chuyên nghiệp với công nghệ Predator mới nhất 2023",
      category: "sports",
      rating: 4.9,
      discount: 0,
      colors: ["Trắng/Đen", "Xanh/Trắng"],
      sizes: [40, 41, 42, 43]
    },
    {
      id: 9,
      name: "Giày Boots Timberland Premium 6-Inch",
      price: 3500000,
      image: "https://images.unsplash.com/photo-1549317661-bd32c8ce0db2?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80",
      description: "Boots da cao cấp chống thấm nước, phong cách rugged, bền bỉ",
      category: "outdoor",
      rating: 4.7,
      discount: 20,
      colors: ["Nâu vàng", "Đen"],
      sizes: [40, 41, 42, 43, 44]
    },
    {
      id: 10,
      name: "Giày Vans Old Skool Classic Black",
      price: 1350000,
      image: "https://images.unsplash.com/photo-1525966222134-fcfa99b8ae77?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80",
      description: "Giày skateboard cổ điển với thiết kế sidestripe biểu tượng",
      category: "sneaker",
      rating: 4.6,
      discount: 10,
      colors: ["Đen", "Trắng", "Xanh Navy"],
      sizes: [38, 39, 40, 41, 42]
    },
    {
      id: 11,
      name: "Giày Thể Thao New Balance 574 Core",
      price: 1950000,
      image: "https://images.unsplash.com/photo-1549298916-b41d501d3772?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80",
      description: "Giày chạy bộ với đế ENCAP êm ái, thiết kế retro đa sắc màu",
      category: "running",
      rating: 4.8,
      discount: 15,
      colors: ["Xám", "Xanh Navy", "Đỏ"],
      sizes: [39, 40, 41, 42, 43]
    },
    {
      id: 12,
      name: "Giày Lười Da Moccasin Handmade",
      price: 1650000,
      image: "https://images.unsplash.com/photo-1560769629-975ec94e6a86?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80",
      description: "Giày lười da mềm mại, handmade tinh xảo, thoải mái cho công sở",
      category: "formal",
      rating: 4.5,
      discount: 25,
      colors: ["Nâu", "Đen", "Nâu đỏ"],
      sizes: [39, 40, 41, 42]
    }
  ];

  // Lọc và sắp xếp sản phẩm
  const filteredAndSortedProducts = useMemo(() => {
    let result = [...featuredProducts];

    // Lọc theo danh mục
    if (category && category !== 'all') {
      result = result.filter(product => product.category === category);
    }

    // Sắp xếp
    switch(sortBy) {
      case 'price-low':
        result.sort((a, b) => (a.price * (1 - a.discount/100)) - (b.price * (1 - b.discount/100)));
        break;
      case 'price-high':
        result.sort((a, b) => (b.price * (1 - b.discount/100)) - (a.price * (1 - a.discount/100)));
        break;
      case 'name':
        result.sort((a, b) => a.name.localeCompare(b.name, 'vi'));
        break;
      case 'rating':
        result.sort((a, b) => b.rating - a.rating);
        break;
      case 'discount':
        result.sort((a, b) => b.discount - a.discount);
        break;
      default:
        // Giữ nguyên thứ tự mặc định
        break;
    }

    return result;
  }, [category, sortBy]);

  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN').format(price) + ' đ';
  };

  const calculateDiscountedPrice = (price, discount) => {
    if (discount === 0) return price;
    return Math.round(price - (price * discount / 100));
  };

  const getCategoryName = (cat) => {
    const categories = {
      'running': 'Chạy bộ',
      'sneaker': 'Sneaker',
      'formal': 'Giày tây',
      'outdoor': 'Ngoài trời',
      'sandals': 'Sandal',
      'sports': 'Thể thao'
    };
    return categories[cat] || cat;
  };

  return (
    <section className="products-section" id="products">
      <div className="section-header">
        <h2 className="section-title">Sản Phẩm Nổi Bật</h2>
        <div className="divider"></div>
        <p className="section-subtitle">
          {category === 'all' 
            ? 'Khám phá 12 mẫu giày được yêu thích nhất hiện nay'
            : `Tìm thấy ${filteredAndSortedProducts.length} sản phẩm`
          }
        </p>
      </div>

      {filteredAndSortedProducts.length > 0 ? (
        <div className="products-grid">
          {filteredAndSortedProducts.map(product => {
            const isWishlisted = wishlist.includes(product.id);
            const discountedPrice = calculateDiscountedPrice(product.price, product.discount);
            
            return (
              <div className="product-card" key={product.id}>
                <div className="product-image">
                  <img src={product.image} alt={product.name} loading="lazy" />
                  {product.discount > 0 && (
                    <span className="discount-badge">-{product.discount}%</span>
                  )}
                  <span className="product-category">
                    <i className="fas fa-tag"></i> {getCategoryName(product.category)}
                  </span>
                  <div className="product-rating">
                    <i className="fas fa-star"></i>
                    <span>{product.rating}</span>
                  </div>
                </div>
                
                <div className="product-info">
                  <h3 className="product-name">{product.name}</h3>
                  
                  <div className="product-features">
                    <div className="feature-item">
                      <i className="fas fa-palette"></i>
                      <span>{product.colors.length} màu</span>
                    </div>
                    <div className="feature-item">
                      <i className="fas fa-shoe-prints"></i>
                      <span>{product.sizes.length} size</span>
                    </div>
                  </div>
                  
                  <div className="price-container">
                    {product.discount > 0 && (
                      <span className="original-price">{formatPrice(product.price)}</span>
                    )}
                    <p className="product-price">{formatPrice(discountedPrice)}</p>
                  </div>
                  
                  <p className="product-description">{product.description}</p>
                  
                  <div className="product-actions">
                    <button 
                      className="view-detail-btn"
                      onClick={() => alert(`Xem chi tiết sản phẩm: ${product.name}`)}
                    >
                      <i className="fas fa-eye"></i> Xem chi tiết
                    </button>
                    <button 
                      className={`quick-action-btn ${isWishlisted ? 'wishlisted' : ''}`}
                      onClick={() => toggleWishlist(product.id)}
                      title={isWishlisted ? "Bỏ yêu thích" : "Thêm vào yêu thích"}
                    >
                      <i className={`fas ${isWishlisted ? 'fa-heart' : 'fa-heart'}`}></i>
                    </button>
                    <button 
                      className="quick-action-btn"
                      onClick={() => addToCart(product.id)}
                      title="Thêm vào giỏ hàng"
                    >
                      <i className="fas fa-shopping-cart"></i>
                    </button>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      ) : (
        <div className="no-products">
          <p>Không tìm thấy sản phẩm nào</p>
        </div>
      )}

      <div className="view-all-container">
        <button 
          className="view-all-btn"
          onClick={() => alert('Tính năng xem tất cả sản phẩm đang phát triển')}
        >
          Xem tất cả sản phẩm <i className="fas fa-arrow-right"></i>
        </button>
      </div>
    </section>
  );
};

export default Products;
